import React, { useState } from "react";
import FetchData from "./component/FetchData";
import User from "./component/User";
import UserList from "./component/UserList";

function App() {
  const [usersList,setusersList] = useState([])
  const [text, settext] = useState(0);
  const[isShow , setisShow] = useState(true)
  const[buttontext,setbuttontext ]= useState(false)
  const [userData,setuserData] = useState([])
  const Increase = () => {
    settext(text + 1);
  };
  const Decrease = () => {
    settext(text - 1);
  };
  const ShowHide = ()=>{
setisShow(!isShow)
setbuttontext(!buttontext)
  }

  const AddUserHandler = (uName,UEmail)=>{
setusersList((prev)=>{
   return [...prev,{name: uName , Email:UEmail}]
})
  }

 async function fetchData (){
      const response = await fetch('https://fakestoreapi.com/products')
      const data = await response.json();
      console.log(data);
      setuserData(data);
    }
  return (
    <>
    <div>
      <h2>{text}</h2>
      <button onClick={Increase}>Increase me!</button>
      <button onClick={Decrease}>Decrease me!</button>
      <br />
      {isShow === true ? <h2>Wecome</h2>: null}
      <button onClick = {ShowHide}>{buttontext? "show": "hide"}</button>
    </div>
    <User addUser={AddUserHandler}/>
    <br/>
    <UserList users = {usersList}/>
    <button onClick={fetchData}>Show Data</button>
    <FetchData userss = {userData} />
    </>
  );
}

export default App;
