import React, { useState } from 'react'

function User(props) {

    const [usrName,setusrName] = useState('')
    const [usrEmail,setusrEmail] = useState('')
    const [isValid,setisValid] = useState(true)

   const addUser = (event)=>{
    event.preventDefault()
    props.addUser(usrName,usrEmail);
    setusrName('')
    setusrEmail('')
   }

   const userName = (event)=>{
     setusrName(event.target.value)
     if(event.target.value.length>5){
       setisValid(false)
     }
   }

   const userEmail = (e)=>{
    setusrEmail(e.target.value)
}

  return (
    <form >
        <label>User name :</label>
        <input type="text" onChange={userName} value={usrName}/>
        <label>Email : </label>
        <input type="text" onChange={userEmail} value={usrEmail}/>
        <button disabled={isValid} type="submit" onClick={addUser}>Add User</button>
    </form>
  )
}

export default User
